binding.script:
print("LZ-129jsb-set.xml: Gaining ballast! (Cheater! ;)");
setprop("/fdm/jsbsim/propulsion/ballast-tank/contents-slug",
        1.05 *
        getprop("/fdm/jsbsim/propulsion/ballast-tank/contents-slug"));
print("Ballast left: ",
      getprop("/fdm/jsbsim/propulsion/ballast-tank/contents-slug"),
      " slug");

print("LZ-129jsb-set.xml: Dropping ballast!");
setprop("/fdm/jsbsim/propulsion/ballast-tank/contents-slug",
        0.95 *
        getprop("/fdm/jsbsim/propulsion/ballast-tank/contents-slug"));
print("Ballast left: ",
      getprop("/fdm/jsbsim/propulsion/ballast-tank/contents-slug"),
      " slug");
